  W1_f28910 == 11 ~ (11001+12000)/2,
  W1_f28910 == 12 ~ (12001+13000)/2,
  W1_f28910 == 13 ~ (13001+14000)/2,
  W1_f28910 == 14 ~ (14001+15000)/2,
