  W1_f21400 %in% c(3) ~ 2, # in training/education
  W1_f21400 %in% c(6) ~ 3, # disability
  W1_f21400 %in% c(4, 8) ~ 4, # no paid work
  W1_f21400 %in% c(5) ~ 5 # retired

),

work_status = case_when(
  W1_f21500 == 4 ~ 1, # self-employed
